html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
    <div class="letter-head">{{ letter_head }}</div>
    {% endif %}

    {% if max_pages > 1 %}
    <p class="text-right">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p>
    {% endif %}
{%- endmacro -%}
{{ add_header(0,1,doc,letter_head, no_letterhead) }}

<h4 class="text-center">
	{{ doc.shift_name|upper }} SHIFT SECURITY ATTENDANCE REPORT FOR {{ frappe.utils.formatdate(doc.shift_date) }}
</h4>

<table class="table table-bordered">
        <thead>
                <tr>
                        <th style="text-align:center">GUARD NAME</th>
                        <th style="text-align:center">POSITION</th>
                        <th style="text-align:center">PRESENT</th>
                        <th style="text-align:center">ABSENT</th>
                        <th style="text-align:center">DAY OFF</th>
                        <th style="text-align:center">REMARKS</th>
                </tr>
        </thead>
        <tbody>
        {% set present = [0] %}
        {% set absent = [0] %}
        {% set day_off = [0] %}
        {% for ad in doc.attendance_details %}
                <tr>
                    {% if ad.status == "Present" %}
                       {% if present.append(present.pop() + 1) %}{% endif %} 
                       {% set cpresent = "" %}
                       {% set cabsent = "" %}
                       {% set cday_off = "" %}
                       {% set cpresent = "X" %}
                    {% elif  ad.status == "Absent" %}
                       {% if absent.append(absent.pop() + 1) %}{% endif %} 
                       {% set cpresent = "" %}
                       {% set cabsent = "" %}
                       {% set cday_off = "" %}
                       {% set cabsent = "X" %}
                    {% elif  ad.status == "Day off" %}
                       {% if day_off.append(day_off.pop() + 1) %}{% endif %} 
                       {% set cpresent = "" %}
                       {% set cabsent = "" %}
                       {% set cday_off = "" %}
                       {% set cday_off  = "X" %}
	            {% else %}		
                       {% set cpresent = "" %}
                       {% set cabsent = "" %}
                       {% set cday_off = "" %}
		    {% endif %}

                        <td>{{ ad.guard_name}}</td>
                        <td>{{ ad.position }}</td>
                        <td style="text-align:center">{{ cpresent }}</td>
                        <td style="text-align:center">{{ cabsent }}</td>
                        <td style="text-align:center">{{ cday_off }}</td>
			<td>{{ ad.remarks }}</td>


                </tr>
        {% endfor %}
        </tbody>
	<tfoot>
		<tr> 
                        <th colspan=2>TOTAL</th>
                        <th style="text-align:center">{{ present[0] }}</th>
                        <th style="text-align:center">{{ absent[0] }}</th>
                        <th style="text-align:center">{{ day_off[0] }}</th>
			<th>&nbsp;</th>
                </tr>

        </tfoot>
</table>
